const SideMenu = ({
  handleSubmit,
  submitStatus,
  submitActive,
  getUrlValidation,
}) => {
  /* crawl all pages */
  /*
    const getAll = async(e) => {
        // clear ui pages table
        resultCallback({status:'clear', data:[]});
        // lock submit
        setState(false);
        const {url, maxDepth, maxPages} = e.target.elements;
        let values = {
            url: url.value,
            maxDepth: maxDepth.value,
            maxPages: maxPages.value
        }
        setStatus("Loading pages...");
        // post data to api 
        let response = await fetch("http://localhost:5000/api/crawl_all", {
            method: 'POST',
            headers: {
                "Content-Type": "application/json;charset=utf-8"
            },
            body: JSON.stringify(values)
        });
        // wait for api response
        let result = await response.json();
        // send results to callback
        resultCallback(result);
        // unlock submit
        setState(true);
        setStatus("Submit");
    }
    */

  return (
    <div className="sideMenu">
      <form onSubmit={handleSubmit}>
        <div className="row mt-1">
          <label htmlFor="url">URL</label>
          <input
            type="text"
            placeholder="ex: https://www.google.com"
            id="url"
            onChange={getUrlValidation}
            required
          />
        </div>
        <div className="row mt-1">
          <label htmlFor="maxDepth">Max Depth</label>
          <input
            type="number"
            min="0"
            defaultValue="0"
            id="maxDepth"
            required
          />
        </div>
        <div className="row mt-1">
          <label htmlFor="maxPages">Max Pages</label>
          <input
            type="number"
            min="1"
            defaultValue="1"
            id="maxPages"
            required
          />
        </div>
        <div className="row mt-3">
          <button type="submit" id="submit_btn" disabled={!submitActive}>
            {submitStatus}
          </button>
        </div>
      </form>
    </div>
  );
};

export default SideMenu;
