import { useState } from "react";
import { isValidUrl } from "../../../util/validator";
import ContentTable from "../../Common/ContentTable";
import SideMenu from "../../Common/SideMenu";

const Crawler = () => {
  const [pages, setPages] = useState([]);

  const resultCallback = (res) => {
    // check result status
    console.log(res);
    if (res.status === "success") {
      // case post success
      setPages((old_pages) => [...old_pages, ...res.pages]);
    } else if (res.status === "clear") {
      // case clear results
      setPages([]);
    }
  };
  const pageCallback = (page) => {
    setPages([...pages, page]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const { url, maxDepth, maxPages } = e.target.elements;
    const options = {
      url: url.value,
      maxDepth: maxDepth.value,
      maxPages: maxPages.value,
      pages: [],
    };
    await startCrawl({ options });
    console.log(state.pages.length);
  };
  const startCrawl = async ({ options }) => {
    const response = await crawlPage({ options });
    if (response.status !== 200) {
      console.log(`[!] Crawl ended with status: ${response.status}`);
      return;
    }
    const result = await response.json();
    setState({ ...state, pages: [...state.pages, result.url] });
    console.log(result);
    if (!result.fin) {
      await startCrawl({ options: result.options });
    }
  };

  return (
    <div className="crawlerPage">
      <div id="form-section" className="column sm">
        <SideMenu
          resultCallback={resultCallback}
          pageCallback={pageCallback}
          isLink={isValidUrl}
          handleSubmit={handleSubmit}
        />
      </div>
      <div id="view-section" className="column bg">
        <div className="row title">
          <h2>RESULTS</h2>
        </div>
        <ContentTable pages={pages} isLink={isValidUrl}></ContentTable>
      </div>
    </div>
  );
};
export default Crawler;
