import React, {useState} from 'react';
import TableSubRow from '../TableSubRow/';
import { ReactComponent as GlobeIcon } from '../../../../assets/Globe_icon.svg'

const TableRow = ({page_index, page})=> {
    const [show, setShow] = useState(false);
    const [status, setStatus] = useState("SHOW");

    /* toggle show/hide page links */
    const toggleLinks = () => {
        setShow(!show);
        if(show) setStatus("SHOW");
        else setStatus("HIDE");
    };
    /* in case no favicon found show default icon */
    const getDefaultIcon = (e) => {
        e.target.src = GlobeIcon
    }
	return (
		<li className="tableRow">
            <div className="row row_container ">
                <ul className="page_container">
                    <li className="p-col" >
                        <img src={page.links.length ? page.links[0].origin + "/favicon.ico" : GlobeIcon} onError={getDefaultIcon} alt="" height="32" width="32"/>
                        <div className="row page-index">
                            <p>{"PAGE " + (page_index+1)}</p>
                        </div>
                    </li>
                    <li className="p-col p-data" >
                        <ul className="page-data">
                            <li><span>TITLE </span>{page.title}</li>
                            <li><span>DEPTH </span>{page.depth}</li>
                            <li><span>URL </span><a href={page.url} rel="noreferrer" target="_blank">{page.url}</a></li>
                        </ul>
                    </li>
                    <li className="p-col show-links" key="page_links_btn">
                        <button onClick={toggleLinks}>{status + " LINKS"}</button>
                    </li>
                </ul>
            </div>
            <div className="row links-container">
                {show ? <TableSubRow links={page.links} def_icon={getDefaultIcon} />: null}
            </div>
        </li>
	)
}

export default TableRow;
