import { useState } from "react";

const ToolTip = ({ children, text }) => {
  const [show, setShow] = useState(false);
  return (
    <div
      className="toolTip"
      onMouseEnter={() => setShow(true)}
      onMouseLeave={() => setShow(false)}
    >
      {children}
      {show && <div className="toolTipText">{text}</div>}
    </div>
  );
};

export default ToolTip;
