import { useState } from 'react';
import { isValidUrl } from "../../../util/validator"
import ContentTable from "../../Common/ContentTable"
import SideMenu from "../../Common/SideMenu"

const Crawler = () => {
    const [pages, setPages] = useState([]);

    const resultCallback = (res) => {
        // check result status
        console.log(res);
        if(res.status === "success") {
            // case post success
            setPages(old_pages => [...old_pages, ...res.pages]);   
        } else if (res.status === "clear"){
            // case clear results
            setPages([]);
        }
    };
    const pageCallback = (res) => {
        // check result status
        console.log(res);
        if(res.status === "success") {
            // case post success
            setPages(old_pages => [...old_pages, res.data.pages[res.data.pages.length - 1]]);   
        }
    }
    return(<div className="crawlerPage">
        <div id="form-section" className="column sm">
                <SideMenu resultCallback={resultCallback} pageCallback={pageCallback} isLink={isValidUrl} />
            </div>
            <div id="view-section" className="column bg">
                <div className="row title">
                    <h2>RESULTS</h2>
                </div>
                <ContentTable pages={pages} isLink={isValidUrl}></ContentTable>
            </div>
    </div>)
}
export default Crawler
